 
function vrepeat(){
	var audio =  $("#voice")[0]; 
	audio.currentTime = 0;//重新播放
}

function vplay(){
	var audio = $("#voice")[0]; 
	if(audio!==null){ 
		if(audio.paused){ 
		audio.play();// 播放 
		}else{
		audio.pause();// 暂停
		}
	} 
}

function vload(){
	var audio = $("#voice")[0]; 
	audio.load();
}
 
$(function(argument) {
	$(".voice-play").click(function(argument) {
		vplay();
	})
})
 
 